import axios from 'axios'
import store from '../store'
import router from '../router'
import { Message } from 'element-ui'

// 创建 axios 实例
const api = axios.create({
  baseURL: '/api',
  timeout: 5000
})

// 请求拦截器
api.interceptors.request.use(
  config => {
    const token = store.state.token
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`
    }
    return config
  },
  error => {
    return Promise.reject(error)
  }
)

// 响应拦截器
api.interceptors.response.use(
  response => {
    return response.data
  },
  error => {
    if (error.response) {
      switch (error.response.status) {
        case 401:
          store.dispatch('logout')
          router.push('/login')
          Message.error('登录已过期，请重新登录')
          break
        case 403:
          Message.error('没有权限执行此操作')
          break
        case 400:
          Message.error(error.response.data.message || '请求参数错误')
          break
        default:
          Message.error('服务器错误')
      }
    } else {
      Message.error('网络错误')
    }
    return Promise.reject(error)
  }
)

export default {
  // 用户相关
  login: data => api.post('/users/login', data),
  register: data => api.post('/users/register', data),
  updatePassword: data => api.put('/users/password', data),
  getUsers: () => api.get('/users'),

  // 消息相关
  getMessages: () => api.get('/messages/my'),
  sendMessage: data => api.post('/messages/send', data),
  sendDepartmentMessage: (deptId, data) => api.post(`/messages/send/department/${deptId}`, data),
  markMessageAsRead: messageId => api.put(`/messages/${messageId}/read`),

  // 任务相关
  getTasks: () => api.get('/tasks/my'),
  getDepartmentTasks: deptId => api.get(`/tasks/department/${deptId}`),
  createTask: data => api.post('/tasks', data),
  updateTaskStatus: (taskId, status) => api.put(`/tasks/${taskId}/status`, { status }),

  // 部门相关
  getDepartments: () => api.get('/departments'),
  createDepartment: data => api.post('/departments', data),
  updateDepartment: (deptId, data) => api.put(`/departments/${deptId}`, data),
  deleteDepartment: deptId => api.delete(`/departments/${deptId}`),
  assignManager: (deptId, managerId) => api.put(`/departments/${deptId}/manager/${managerId}`)
}
