import request from '@/utils/request'

export default {
  // 创建请假申请
  createLeave: (data) => request({
    url: '/leaves',
    method: 'post',
    data
  }),

  // 审批请假
  approveLeave: (leaveId, data) => request({
    url: `/leaves/${leaveId}/approve`,
    method: 'post',
    data
  }),

  // 获取我的请假记录
  getMyLeaves: () => request({
    url: '/leaves/my',
    method: 'get'
  }),

  // 获取待我审批的请假
  getPendingApprovals: () => request({
    url: '/leaves/pending',
    method: 'get'
  }),

  // 获取部门请假记录
  getDepartmentLeaves: (deptId) => request({
    url: `/leaves/department/${deptId}`,
    method: 'get'
  }),

  // 获取请假详情
  getLeaveById: (leaveId) => request({
    url: `/leaves/${leaveId}`,
    method: 'get'
  })
}
