import request from '@/utils/request'

const api = {
  // 用户相关
  login: (data) => request.post('/auth/login', data),
  register: (data) => request.post('/auth/register', data),
  updatePassword: (data) => request.put('/users/password', data),
  getUsers: () => request.get('/users'),
  updateUser: (userId, data) => request.put(`/users/${userId}`, data),
  resetPassword: (userId) => request.post(`/users/${userId}/reset-password`),
  updateUserStatus: (userId, status) => request.put(`/users/${userId}/status`, { status }),
  deleteUser: (userId) => request.delete(`/users/${userId}`),

  // 消息相关
  message: {
    getMessages: () => request.get('/messages/my'),
    getSentMessages: () => request.get('/messages/sent'),
    sendMessage: (data) => request.post('/messages', data),
    sendDepartmentMessage: (deptId, data) => request.post(`/messages/department/${deptId}`, data),
    markAsRead: (messageId) => request.put(`/messages/${messageId}/read`)
  },

  // 任务相关
  task: {
    getMyTasks: () => request.get('/tasks/my'),
    getDepartmentTasks: (deptId) => request.get(`/tasks/department/${deptId}`),
    createTask: (data) => request.post('/tasks', data),
    updateStatus: (taskId, status) => request.put(`/tasks/${taskId}/status`, status)
  },

  // 请假相关
  leave: {
    getMyLeaves: () => request.get('/leaves/my'),
    getPendingApprovals: () => request.get('/leaves/pending'),
    createLeave: (data) => request.post('/leaves', data),
    approveLeave: (leaveId, data) => request.post(`/leaves/${leaveId}/approve`, data)
  },

  // 部门相关
  department: {
    getDepartments: () => request.get('/departments'),
    createDepartment: (data) => request.post('/departments', data),
    updateDepartment: (deptId, data) => request.put(`/departments/${deptId}`, data),
    deleteDepartment: (deptId) => request.delete(`/departments/${deptId}`),
    assignManager: (deptId, managerId) => request.put(`/departments/${deptId}/manager/${managerId}`)
  },

  // 公告相关
  announcement: {
    getAnnouncements: (all = false) => request.get(`/announcements${all ? '?all=true' : ''}`),
    createAnnouncement: (data) => request.post('/announcements', data),
    updateStatus: (id, status) => request.put(`/announcements/${id}/status`, status)
  }
}

export default api
